import React, { Component, Fragment } from "react"

import { Button, ButtonGroup, ButtonToolbar } from 'react-bootstrap-buttons';

import Modal from 'react-bootstrap/Modal';

export default class UserItem extends Component {

    constructor(props) {
        super(props)
        this.state = {
            name: props.user.name,
            phone: props.user.phone,
            isEdit: false
        }
    }

    handleInputChange = (event) => {
        const target = event.target;
        const value = target.type === 'checkbox' ? target.checked : target.value;
        const name = target.name;

        this.setState({
            [name]: value,
        });
    }

    handleEdit = () => {
        this.setState({
            isEdit: true
        })
    }

    handleCancel = () => {
        this.setState({
            isEdit: false
        })
    }

    saveEdit = () => {
        this.props.update(
            this.state.name,
            this.state.phone
        )
        this.setState({
            isEdit: false
        })
    }
    cancelHandleModalShowHide() {
        this.setState({
            showHide: false
        })
    }

    render() {
        return (
            <Fragment>
            <tr>
                <td>{this.props.no}</td>
                <td>
                    {this.state.isEdit ?
                        <input
                            className="form-control"
                            type="text"
                            name="name"
                            value={this.state.name}
                            placeholder="Masukkan nama"
                            onChange={this.handleInputChange}
                        /> 
                        :
                        this.state.name
                    }
                </td>
                <td>
                    {this.state.isEdit ?
                        <input
                            className="form-control"
                            type="text"
                            name="phone"
                            value={this.state.phone}
                            placeholder="Masukkan nomor"
                            onChange={this.handleInputChange}
                        /> 
                        :
                        this.state.phone

                    }
                </td>
                {this.props.user.sent ?
                    this.state.isEdit ?
                        <td>
                            <button type="button" className="btn btn-info" onClick={this.saveEdit}>Save</button>
                            <button type="button" className="btn btn-warning" onClick={this.handleCancel}>Cancel</button>
                        </td>
                        :
                        <td>
                            <button type="button" className="btn btn-success" onClick={this.handleEdit}>Edit</button>
                            <button type="button" className="btn btn-danger" onClick={this.props.remove}>Delete</button>
                        </td>
                    :
                    <td>
                        <button type="button" className="btn btn-warning" onClick={this.props.resend}>Resend</button>
                    </td>
                }
            </tr>
             <Modal show={this.state.showHide}>
             <Modal.Header >
                 <Modal.Title>Deleted Confirmation</Modal.Title>
             </Modal.Header>
             <Modal.Body>Are you sure, you want delete it</Modal.Body>
             <Modal.Footer>
                 <Button variant="secondary" onClick={() => this.cancelHandleModalShowHide()}>
                     No
                 </Button>
                 <Button variant="primary" onClick={this.props.remove}>
                     Yes
                 </Button>
             </Modal.Footer>
         </Modal>

     </Fragment>

        )
    }
}
