import { Component } from "react";
import UserForm from "./UserForm";
import UserList from "./UserList";

export default class UserBox extends Component {

    constructor(props) {
        super(props)

        this.state = {
            users: []
        }
    }

    componentDidMount() {
        fetch('http://localhost:3000/users')
            .then((response) => response.json())
            .then((data) => {
                this.setState({
                    users: data.data.map(item => {
                        item.sent = true
                        return item
                    })
                })
            })
    }

    addUser = ({ name, phone }) => {
        const id = Date.now().toString()
        this.setState(function (state, props) {
            return {
                users: [
                    ...state.users,
                    {
                        id,
                        name,
                        phone,
                        sent: true
                    }
                ]
            }
        })
        fetch('http://localhost:3000/users', {
            method: 'POST',
            headers: {
                'Content-Type': 'application/json',
            },
            body: JSON.stringify({ name, phone }),
        })
            .then((response) => response.json())
            .then((data) => {
                this.setState(function (state, props) {
                    return {
                        users: state.users.map(item => {
                            if (item.id === id) {
                                return {
                                    id: data.data.id,
                                    name: data.data.name,
                                    phone: data.data.phone,
                                    sent: true
                                }
                            }
                            return item
                        })
                    }
                })
            })
            .catch((error) => {
                this.setState(function (state, props) {
                    return {
                        users: state.users.map(item => {
                            if (item.id === id) {
                                return {
                                    ...item,
                                    sent: false
                                }
                            }
                            return item
                        })
                    }
                })
            })
    }

    updateUser = (id, name, phone) => {
        fetch(`http://localhost:3000/users/${id}`, {
            method: 'POST',
            headers: {
                'Content-Type': 'application/json',
            },
            body: JSON.stringify({ name, phone }),
        })
            .then((response) => response.json())
            .then((data) => {
                this.setState(function (state) {
                    return {
                        users: state.users.map(item => {
                            if (item.id === id) {
                                return {
                                    id: data.data.id,
                                    name: data.data.name,
                                    phone: data.data.phone,
                                    sent: true
                                }
                            }
                            return item
                        })
                    }
                })
            })
    }

    searchUser = (query = {}) => {
        fetch(`http://localhost:3000/users?${new URLSearchParams(query)}`)
            .then((response) => response.json())
            .then((data) => {
                this.setState({
                    users: data.data.map(item => {
                        item.sent = true
                        return item
                    })
                })
            })
    }

    resendUser = ({ id, name, phone }) => {
        fetch('http://localhost:3000/users', {
            method: 'POST',
            headers: {
                'Content-Type': 'application/json',
            },
            body: JSON.stringify({ name, phone }),
        })
            .then((response) => response.json())
            .then((data) => {
                this.setState(function (state, props) {
                    return {
                        users: state.users.map(item => {
                            if (item.id === id) {
                                return {
                                    id: data.data.id,
                                    name: data.data.name,
                                    phone: data.data.phone,
                                    sent: true
                                }
                            }
                            return item
                        })
                    }
                })
            })
            .catch((error) => {
                console.log('gagal resend')
            })
    }

    removeUser = (id) => {
        fetch(`http://localhost:3000/users/${id}`, {
            method: 'DELETE',
            headers: {
                'Content-Type': 'application/json',
            }
        }).then((response) => response.json()).then((data) => {
            this.setState(function (state, props) {
                return {
                    users: state.users.filter(item => item.id !== id)
                }
            })
        })
    }

    render() {
        return (
            <div className="container">
                <div className="card">
                    <div className="card-header">
                        <h1>Phone Book Apps</h1>
                    </div>
                    <div className="card-body">
                        <div className="card">
                            <div className="card-header">
                                <strong>Searching</strong>
                            </div>
                            <div className="card-body">
                                <UserForm submit={this.searchUser} submitLabel="search" />
                            </div>
                        </div>
                        <hr />
                        <UserForm submit={this.addUser} />
                    </div>
                    <UserList
                        data={this.state.users}
                        remove={this.removeUser}
                        resend={this.resendUser}
                        update={this.updateUser}
                    />
                    <div className="card-footer">

                    </div>
                </div>
            </div>

        )
    }
}
