import { Component } from "react";

export default class UserForm extends Component {

    constructor(props) {
        super(props)
        this.state = {
            name: '',
            phone: ''
        }
    }

    handleInputChange = (event) => {
        const target = event.target;
        const value = target.type === 'checkbox' ? target.checked : target.value;
        const name = target.name;

        this.setState({
            [name]: value
        });
    }

    handleSubmit = (event) => {
        event.preventDefault()
        this.props.add({ name: this.state.name, phone: this.state.phone })
        this.setState({ name: '', phone: '' })
    }

    handleSearch = (event) => {
        event.preventDefault()
        this.props.submit({ name: this.state.name, phone: this.state.phone })
    }

    handleSearchCancel = (event) => {
        console.log(event)
        event.preventDefault()
        this.props.cancel()
        this.setState({ name: '', phone: '' })

    }

    render() {
        return (
            <form onSubmit={this.props.submitLabel ? this.handleSearch : this.handleSubmit}>
                <div className="row g-1 align-items-center">
                    <div className="col-auto">
                        <label
                            htmlFor="name"
                            className="col-form-label">
                            <strong>Name</strong>
                        </label>
                    </div>
                    <div className="col-auto">
                        <input
                            type="teks"
                            id="name"
                            name="name"
                            className="form-control"
                            onChange={this.handleInputChange}
                            value={this.state.name}
                        />
                    </div>

                    <div className="col-auto">
                        <label
                            htmlFor="phone"
                            className="col-form-label">
                            <strong>Phone</strong>
                        </label>
                    </div>
                    <div className="col-auto">
                        <input
                            type="teks"
                            id="phone"
                            name="phone"
                            className="form-control"
                            onChange={this.handleInputChange}
                            value={this.state.phone}
                        />
                    </div>

                    <div className="col-auto">
                        <button type="submit" className="btn btn-info" >
                            {this.props.submitLabel || "add"} </button>
                        &nbsp;
                        {this.props.submitLabel !== "add" &&
                            <button type="submit"
                                onClick={this.handleInputChange}
                                className="btn btn-warning"
                                style={{ color: "black" }}>
                                <i className="fa-solid fa-ban"></i>
                                &nbsp;
                                cancel</button>
                        }

                    </div>
                </div>
            </form>
        )
    }
}
